toolUseResult.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 20 20% 98%;
    --foreground: 20 20% 10%;
    --card: 0 0% 100%;
    --card-foreground: 20 20% 10%;
    --popover: 0 0% 100%;
    --popover-foreground: 20 20% 10%;
    --primary: 12 80% 55%;
    --primary-foreground: 0 0% 100%;
    --secondary: 20 15% 94%;
    --secondary-foreground: 20 20% 20%;
    --muted: 20 10% 92%;
    --muted-foreground: 20 10% 45%;
    --accent: 35 90% 55%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 75% 55%;
    --destructive-foreground: 0 0% 100%;
    --border: 20 15% 88%;
    --input: 20 15% 88%;
    --ring: 12 80% 55%;
    --radius: 1rem;

    /* Custom tokens */
    --success: 145 65% 42%;
    --success-foreground: 0 0% 100%;
    --warning: 35 90% 50%;
    --warning-foreground: 0 0% 100%;
    --danger: 0 75% 55%;
    --danger-foreground: 0 0% 100%;

    /* Shadows */
    --shadow-primary: 0 10px 40px -10px hsl(12 80% 55% / 0.4);
    --shadow-success: 0 10px 40px -10px hsl(145 65% 42% / 0.4);
    --shadow-card: 0 4px 20px -4px hsl(20 20% 10% / 0.08);
  }

  .dark {
    --background: 20 15% 8%;
    --foreground: 20 10% 95%;
    --card: 20 15% 12%;
    --card-foreground: 20 10% 95%;
    --popover: 20 15% 12%;
    --popover-foreground: 20 10% 95%;
    --primary: 12 80% 60%;
    --primary-foreground: 0 0% 100%;
    --secondary: 20 15% 18%;
    --secondary-foreground: 20 10% 90%;
    --muted: 20 15% 18%;
    --muted-foreground: 20 10% 60%;
    --accent: 35 90% 55%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 70% 50%;
    --destructive-foreground: 0 0% 100%;
    --border: 20 15% 20%;
    --input: 20 15% 20%;
    --ring: 12 80% 60%;

    /* Custom tokens */
    --success: 145 60% 45%;
    --success-foreground: 0 0% 100%;
    --warning: 35 85% 55%;
    --warning-foreground: 0 0% 100%;
    --danger: 0 70% 55%;
    --danger-foreground: 0 0% 100%;

    /* Shadows */
    --shadow-primary: 0 10px 40px -10px hsl(12 80% 60% / 0.3);
    --shadow-success: 0 10px 40px -10px hsl(145 60% 45% / 0.3);
    --shadow-card: 0 4px 20px -4px hsl(0 0% 0% / 0.3);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer utilities {
  .gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--accent)));
  }
  .gradient-success {
    background: linear-gradient(135deg, hsl(var(--success)), hsl(160 60% 45%));
  }
  .gradient-danger {
    background: linear-gradient(135deg, hsl(var(--danger)), hsl(var(--primary)));
  }
  .shadow-primary {
    box-shadow: var(--shadow-primary);
  }
  .shadow-success {
    box-shadow: var(--shadow-success);
  }
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
}

